import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

const Update = (pid) => {
    const [data,setData] = useState([])
    const [id,setPid] = useState()
    const [name,setPname] = useState()
    const [address,setPaddrs] = useState()
    const [rent,setPrent] = useState()
    const [property_details,setPdetails] = useState()

    const location = useLocation() 
    let dataId = location.state.id

    const getData = async() => {
        // e.preventDefault();
        console.log(location.state.id)
        const resp = await axios.get(`https://63610b45af66cc87dc21409b.mockapi.io/property_data/${dataId}`)
        const searchData = resp.data
        setData(searchData)    
        debugger
    }
    useEffect(()=>{
        getData()
        updateUser(dataId)
        },[]) 

    const updateData = async(e,dataId) => {
        e.preventDefault();
        let item = {
            name: name,
            address: address,
            rent: rent,
            property_details: property_details,
        }
        debugger
        const response = await axios.put(`https://63610b45af66cc87dc21409b.mockapi.io/property_data/${dataId}`, item);
        let data = response.data;
        console.log(response);
        debugger
        getData()


    }


    const updateUser = (dataId) =>{
        let updData = dataId
        console.warn(dataId)
        setPid(updData.id)
        setPname(updData.name)
        setPaddrs(updData.address)
        setPrent(updData.rent)
        setPdetails(updData.property_details)
    }



    return (
        <>
          <form>
                <table>
            <pre>
                ID            : <input  readOnly value={id}/><br/>
                Property Name : <input onChange={(e)=>{setPname(e.target.value)}} value={name}/><br/>
                Address       : <input onChange={(e)=>{setPaddrs(e.target.value)}} value={address}/><br/>
                Rent          : <input onChange={(e)=>{setPrent(e.target.value)}} value={rent}/><br/>
                Details       : <input onChange={(e)=>{setPdetails(e.target.value)}} value={property_details}/><br/>
                <button onClick={()=>updateData(data.id)}>Update Data</button>
            </pre>
            </table>
        </form>
        </>
    )
}
export default Update;
