import axios from "axios";
import React from "react";
import { useState } from "react";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Project = () => {

    let Navigate = useNavigate()
    const [projectData,setPdata] = useState([])


    const propsData = (JSON.parse(localStorage.getItem('headers')));
    const userData = (JSON.parse(localStorage.getItem('data')));
    console.log('user data = ',userData.data)

    let hed = {
        "uid": propsData.uid,
        "client": propsData.client,
        "access-token": propsData["access-token"]
    }
    let headersData = {
        headers: hed,
    }

    const getProjectData = async() =>{

        console.warn('PropesData = ',(headersData))

        const resp = await axios.get('http://localhost:4000/projects',headersData)
        // debugger
        console.warn(resp)
        setPdata(resp.data)
    }
    const gotoCreateProject = () => {
        let path = '/createProject'
        Navigate(path, {state: {headerData: propsData, user: userData}})
    }
    const arry = Object.keys(projectData)

    useEffect(()=>{
        Check()
    },[])

    const deleteData = async(id) => {
        await axios.delete("http://localhost:4000/projects",{data:id},headersData)
        Navigate('/Project')
    }

    const Check = () => {
        if (propsData != null){
            getProjectData()
        }
        else {
            Navigate('/Login')
        }
    }

    return (
        <>
            <pre>
                        <h3>Welcom {userData.data.full_name}</h3>
                        <button onClick={gotoCreateProject}>Create New Data</button>
                        <table>
                           <thead>
                            <th>ID</th>
                            <th>COMPANY</th>
                            <th>PROJECT</th>
                            <th>DESCRIPTION</th>
                           </thead>
                           <tbody>
                {projectData.map((value)=>{
                    return(
                        console.log('projectData',projectData),
                       <>
                            <tr>
                                <td>{value.id}</td>
                                <td>{value.company_name}</td>
                                <td>{value.project_name}</td>
                                <td>{value.description}</td>
                                <td><button onClick={()=>{deleteData(value.id)}}>Delete</button></td>
                            </tr>
                        </>
                            )})}

                        </tbody>
                        </table>    

                {/* <p>
                    USER ID     : {projectData.user_id}
                    COMPANY     : {projectData.company_name}
                    PROJECT     : {projectData.projet_name}
                    DESCRIPTION : {projectData.description}
                </p> */}
            </pre>
        </>
    )
}


export default Project;
