import axios from "axios";
import React from "react";
import { useState } from "react";
import { useLocation } from "react-router-dom";


const CreateProject = () => {
    const [Pname,setPname] = useState()
    const [PCname,setPCname] = useState()
    const [Pdesc,setPdesc] = useState()
    const location = useLocation()
    let userHeader = location.state.headerData
    console.log('userHeader = ',userHeader)
    let hed = {
        "uid": userHeader['uid'],
        "client": userHeader['client'],
        "access-token": userHeader["access-token"]
    }
    let Pbody = {
        "project_name": Pname,
        "company_name": PCname,
        "description": Pdesc
    }

    debugger
    let Pdata = {
        headers:hed,
        body: {"project":Pbody}
    }

    // console.log('userData = ',user)
    const sendData = async(e) =>{
        e.preventDefault()
        const resp = await axios.post('http://localhost:4000/projects',Pdata)

    }

    return(
        <>
        <h1>Create Project</h1>
        <form>
        <pre>
            Project Name : <input type="text" placeholder="Enter Project Name" onChange={setPname} />
            Company Name : <input type="text" placeholder="Enter Company Name" onChange={setPCname} />
            Description  : <input type="text" placeholder="Enter Description" onChange={setPdesc} />

            <button onClick={sendData}>Create Data</button>
        </pre>
        </form>
        </>
    )
}

export default CreateProject;
