    }

    const getData = async() => {
        const res = await fetch("https://63610b45af66cc87dc21409b.mockapi.io/property_data")
        const actualData = await res.json();
        setData(actualData);
        console.log('data',actualData)
    }

    const arry = Object.keys(data)

    useEffect(()=>{
    getData()
    },[]) 

    return (
        <>

        <h1>Rental Properties by Shreyansh</h1>
        <button onClick={gotoCreate}>Create new property data</button>
        <button onClick={gotoSearch}>Search Property by ID</button>
        <table>
            <thead>
                <tr>
                    <th>Sr. no</th>
                <th>Name of Property</th>
                <th>Adress of Property</th>
                <th>Property rent</th>
                <th>Property Details</th>
                </tr>
            </thead>
            <tbody>
        {arry.map((value)=>{
            // console.log('final map data',data[value])
            return (
                <>
                <tr>
                    <td>{data[value].id}</td>
                    <td>{data[value].name}</td>
                    <td>{data[value].address}</td>
                    <td>{data[value].rent}</td>
                    <td>{data[value].property_details}</td>
                    <button onClick={()=>updateUser(data[value].id)}>Update</button>
                    <button onClick={()=> deletePost(data[value].id)}>Delete</button>
                </tr>
                </>
                )
            })}
        </tbody>
            </table>
            <form>
            <pre>
                ID : <input value={Pid}/>
                Property Name : <input value={Pname}/>
                Address : <input value={Paddrs}/>
                Rent : <input value={Prent}/>
                Details : <input value={Pdetails}/>
                <button></button>
            </pre>
        </form>
        </>
    )
}



export default Base;
