    let Navigate = useNavigate()

    const gotoCreate = () =>{
        let path = '/create'
        Navigate(path);
            }

    const gotoSearch = () =>{
        let path = '/Search'
        Navigate(path)
    }

    const deletePost = (id) => {
        fetch(`https://63610b45af66cc87dc21409b.mockapi.io/property_data/${id}`,
        {
            method:"DELETE"
        }).then((result)=>{
            result.json().then((resp)=>{
                console.warn(resp)
                getData()
            })
        })
    }
    // const updateData = async(e) => {
    //     e.preventDefault();
