import React from 'react';
import { useState,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';


const Base = () => {

    const [data,setData] = useState([])
    const [Pid,setPid] = useState()
    const [Pname,setPname] = useState()
    const [Paddrs,setPaddrs] = useState()
    const [Prent,setPrent] = useState()
    const [Pdetails,setPdetails] = useState()


    let Navigate = useNavigate()

    const gotoCreate = () =>{
        let path = '/create'
        Navigate(path);
            }

    const gotoSearch = () =>{
        let path = '/search'
        Navigate(path)
    }

    const deletePost = (id) => {
        fetch(`https://63610b45af66cc87dc21409b.mockapi.io/property_data/${id}`,
        {
            method:"DELETE"
        }).then((result)=>{
            result.json().then((resp)=>{
                console.warn(resp)
                getData()
            })
        })
    }

    const updateUser = (id) =>{
        let updData = data[id-1] 
        console.warn(data[id-1])
        setPid(updData.id)
        setPname(updData.name)
        setPaddrs(updData.address)
        setPrent(updData.rent)
        setPdetails(updData.property_details)
    }

    const getData = async() => {
        const res = await fetch("https://63610b45af66cc87dc21409b.mockapi.io/property_data")
        const actualData = await res.json();
        setData(actualData);
        console.log('data',actualData)
    }

    const arry = Object.keys(data)

    useEffect(()=>{
    getData()
    },[]) 

    return (
        <>

        <h1>Rental Properties by Shreyansh</h1>
        <button onClick={gotoCreate}>Create new property data</button>
        <button onClick={gotoSearch}>Search Property by ID</button>
        <table>
            <thead>
                <tr>
                    <th>Sr. no</th>
                <th>Name of Property</th>
                <th>Adress of Property</th>
                <th>Property rent</th>
                <th>Property Details</th>
                </tr>
            </thead>
            <tbody>
        {arry.map((value)=>{
            // console.log('final map data',data[value])
            return (
                <>
                <tr>
                    <td>{data[value].id}</td>
                    <td>{data[value].name}</td>
                    <td>{data[value].address}</td>
                    <td>{data[value].rent}</td>
                    <td>{data[value].property_details}</td>
                    <button onClick={()=>updateUser(data[value].id)}>Update</button>
                    <button onClick={()=> deletePost(data[value].id)}>Delete</button>
                </tr>
                </>
                )
            })}
        </tbody>
            </table>
            <form>
                <table>
            <pre>
                ID : <input value={Pid}/><br/>
                Property Name : <input value={Pname}/><br/>
                Address : <input value={Paddrs}/><br/>
                Rent : <input value={Prent}/><br/>
                Details : <input value={Pdetails}/><br/>
                <button></button>
            </pre>
            </table>
        </form>
        </>
    )
}



export default Base;
