import React from 'react';
import { useState,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';


const Base = () => {

    const [data,setData] = useState([])
    let Navigate = useNavigate()
    const gotoCreate = () =>{
        let path = '/create'
        Navigate(path);
    }

    const deletePost = (id) => {
        console.log(id)
        data.delete(`${id}`);
        setData(
           data.filter((data) => {
              return data.id !== id;
           })
        );
     };

    deleteData(id) 
     {
        alert("Deleted....")
    //     fetch('https://63610b45af66cc87dc21409b.mockapi.io/property_data'+id,
    //     {
    //         method: "DELETE",
    //     }).then((result)=>{
    //         result.json().then((resp)=>{
    //             alert('Data Deleted')
    //         })
    //     })

     };

    const getData = async() => {
        const res = await fetch("https://63610b45af66cc87dc21409b.mockapi.io/property_data")
        const actualData = await res.json();
        setData(actualData);
        console.log('data',actualData)
    }

    const arry = Object.keys(data)

    useEffect(()=>{
    getData()
    },[]) 

    return (
        <>

        <h1>Rental Properties by Shreyansh</h1>
        <button onClick={gotoCreate}>Create new property data</button>
        <button>Search Property by ID</button>
        <table>
            <thead>
                <tr>
                    <th>Sr. no</th>
                <th>Name of Property</th>
                <th>Adress of Property</th>
                <th>Property rent</th>
                <th>Property Details</th>
                </tr>
            </thead>
            <tbody>
        {arry.map((value)=>{
            // console.log('final map data',data[value])
            return (
                <>
                <tr>
                    <td>{data[value].id}</td>
                    <td>{data[value].name}</td>
                    <td>{data[value].address}</td>
                    <td>{data[value].rent}</td>
                    <td>{data[value].property_details}</td>
                    <button>Update</button>
                    <button onClick={()=>this.deleteData(data[value].id)}>Delete</button>
                </tr>
                </>
                )
            })}
        </tbody>
            </table>
        </>
    )
}


export default Base;
